<style>
table {
  text-align: center;
}

td {
  font-size: 20px;
  padding-left: 175px;
}

th {
  padding-left: 175px;
  font-size: 25px;
}
.day {
  background: rgb(255, 255, 255);
  background: linear-gradient(
    90deg,
    rgba(255, 255, 255, 1) 0%,
    rgba(255, 255, 255, 1) 20%,
    rgba(0, 160, 249, 1) 100%
  );
  width: 100%;
  padding-top: 10px;
  margin: 0;
  border-radius: 25px;
  position: relative;
}

.body {
  background-image: url("https://wallpaperaccess.com/full/2514318.jpg");
  position: relative;
  top: 10;
  left: 0;
  /* Preserve aspet ratio */
  min-width: 100%;
  min-height: 100%;
}

.dd {
  padding-top: 60px;
  margin-left: -291px;
  font-size: 30px;
}

.status {
  background: rgb(255, 255, 255);
  background: linear-gradient(
    90deg,
    rgba(255, 255, 255, 1) 0%,
    rgba(255, 255, 255, 1) 20%,
    rgba(0, 160, 249, 1) 100%
  );
  width: 1150px;
  padding: 10px;
  margin: 0;
  border-radius: 25px;
  position: relative;
  left: 180px;
}
</style>
<template>
  <div class="body">
    <div id="app">
      <br />
      <div class="media mt-4" style="margin-left: 36%">
        <figure class="media-left">
          <p class="image is-64x64">
            <img
              src="data:image/png;base64,"
              class="is-rounded"
              alt="error"
            />
          </p>
        </figure>
        <div class="media-right mt-3">
          <p class="title">USER PROFILE</p>
        </div>
      </div>
      <br />
      <div class="day">
        <div class="media mt-4">
          <figure class="media-left">
            <figure class="image is-128x128 ml-6">
              <img :src="imagePath(this.users[0].picture)" />
            </figure>
          </figure>
          <div class="media-right mt-3 is-size-3">
            <p>Username: {{ this.users[0].username }}</p>
            <p>Email: {{ this.users[0].email }}</p>
          </div>
        </div>
        <p class="title has-text-centered">Booking Product</p>
        <table class="has-text-centered">
          <tr>
            <th>Product</th>
            <th>Amount</th>
            <th>Price</th>
            <th>Total</th>
          </tr>
          <tr v-for="product in selectProducts" :key="product.id">
            <td>{{ product.name }}</td>
            <td>{{ product.amount }}</td>
            <td>{{ product.price }}</td>
            <td></td>
          </tr>
          <tr>
            <td></td>
            <td></td>
            <td></td>
            <td>
              <b>{{ pricePro }}</b>
            </td>
          </tr>
        </table>
        <hr class="has-background-dark" />
        <p class="title has-text-centered mt-6">Booking Queue</p>
        <b class="ml-6">CODE</b>
        <div class="media">
          <div class="media-left ml-6 has-background-danger" v-if="edit">
            Your Code :
            <b>{{ this.checkCode }} is discount for {{ percent }}%</b>
          </div>
          <div class="media-left ml-6" v-if="selectt">
            <select v-model="code" @click="check()">
              <option v-for="code in promotions" :key="code.id">
                {{ code.promotion_code }}
              </option>
            </select>
          </div>
        </div>
        <div class="ml-6 mt-2" v-if="ok">
          <button class="button is-success" @click="confirm()">Confirm</button>
        </div>
        <div class="ml-6 mt-2" v-if="edit">
          <button class="button is-dark" @click="modal = !modal">
            Change your code
          </button>
        </div>
        <table>
          <tr>
            <th>Field</th>
            <th>Day</th>
            <th>Time</th>
            <th>Price</th>
            <th>Total</th>
            <th></th>
          </tr>
          <br />
          <tr v-for="nd in this.stadium1" :key="nd.id">
            <td>{{ nd.stadium }}</td>
            <td>{{ nd.day }}</td>
            <td>{{ nd.time }}</td>
            <td>{{ nd.price }}</td>
            <td></td>
            <td v-if="countst1 > 0">
              <button class="button is-danger mb-1" @click="cancelst1(nd)">
                Cancel
              </button>
            </td>
          </tr>
          <tr v-for="nd in this.stadium2" :key="nd.id">
            <td>{{ nd.stadium }}</td>
            <td>{{ nd.day }}</td>
            <td>{{ nd.time }}</td>
            <td>{{ nd.price }}</td>
            <td></td>
            <td v-if="countst2 > 0">
              <button class="button is-danger mb-1" @click="cancelst2(nd)">
                Cancel
              </button>
            </td>
          </tr>
          <tr>
            <td></td>
            <td></td>
            <td></td>
            <td></td>
            <td>
              <b>{{ editTo }}</b>
            </td>
          </tr>
        </table>
        <hr class="has-background-dark" />
        <div class="has-text-centered">
          <p class="is-size-3"><b>TotalAll</b> {{ pricePro + editTo }}</p>

          <p class="is-size-3" style="margin-left: 9% width: 100px">
            <b>Status</b>
            <span
              class="has-background-danger ml-3"
              v-if="this.users[0].status == 'NotPay'"
              >{{ this.users[0].status }}</span
            >
            <span class="has-background-success ml-3" v-else>{{
              this.users[0].status
            }}</span>
          </p>
        </div>
      </div>
      <div class="modal" :class="{ 'is-active': modal }">
        <div class="modal-background"></div>
        <div class="modal-card">
          <header class="modal-card-head">
            <p class="modal-card-title">Change Code</p>
            <button
              class="delete"
              aria-label="close"
              @click="modal = !modal"
            ></button>
          </header>
          <section class="modal-card-body">
            <b>Code</b>
            <select v-model="checkCode" class="input">
              <option v-for="code in promotions" :key="code.id">
                {{ code.promotion_code }}
              </option>
            </select>
          </section>
          <footer class="modal-card-foot">
            <button class="button is-success" @click="editCode()">
              Save changes
            </button>
            <button class="button" @click="modal = !modal">Cancel</button>
          </footer>
        </div>
      </div>
    </div>
  </div>
</template>
<script>
import axios from "axios";

export default {
  props: ["user"],
  data() {
    return {
      modal: false,
      status: [],
      users: null,
      modalImage: false,
      images: [],
      total: 0,
      editTo: 0,
      sumTo: 0,
      checkCode: "",
      //booking
      stadium1: [],
      stadium2: [],
      countst1: 0,
      countst2: 0,

      //promotions
      promotions: [],
      code: "",

      ok: false,
      edit: false,
      selectt: true,

      percent: 0,
      pricePro: 0,
    };
  },
  mounted() {
    this.getBlogs();
  },
  methods: {
    axios,

    getBlogs() {
      axios
        .get("http://localhost:3000")
        .then((response) => {
          this.users = this.user;
          console.log({ user: this.users });
          const summ = this.users[0].amount;
          this.editTo = summ;
          this.stadium1 = response.data.stadium1.filter(
            (res) => res.username == this.users[0].username
          );

          this.stadium2 = response.data.stadium2.filter(
            (res) => res.username == this.users[0].username
          );

          this.countst1 = this.stadium1.length;
          this.countst2 = this.stadium2.length;
          console.log({ st1: this.countst1, st2: this.countst2 });

          for (let index1 = 0; index1 < this.stadium1.length; index1++) {
            const element = this.stadium1[index1];
            this.total += element.price;
            this.sumTo += element.price;
          }
          for (let index = 0; index < this.stadium2.length; index++) {
            const element = this.stadium2[index];
            this.total += element.price;
            this.sumTo += element.price;
          }
          this.checkCode = this.users[0].code;

          if (this.checkCode != "") {
            this.edit = true;
            this.selectt = false;
          } else {
            this.updateAmount();
            console.log({ price: this.users[0].amount });
            this.editTo = this.users[0].amount;
          }
          this.promotions = response.data.promotions;
          const pp = this.promotions;
          const discount = pp.filter(
            (res) => res.promotion_code == this.checkCode
          );
          this.percent = discount[0].promotion_rate;
          const pro = response.data.selectProduct;
          this.selectProducts = pro.filter(
            (res) => res.username == this.users[0].username
          );
          for (let index = 0; index < this.selectProducts.length; index++) {
            const element = this.selectProducts[index];
            const price = element.price * element.amount;
            this.pricePro += price;
          }
        })
        .catch((err) => {
          console.log(err);
        });
    },
    editCode() {
      this.total = this.sumTo;
      this.code = this.checkCode;
      alert("Success Change Code");
      if (this.code == "") {
        this.total = this.total;
      } else {
        const promo = this.promotions.filter(
          (res) => res.promotion_code == this.code
        );
        const amount = (this.total * promo[0].promotion_rate) / 100;
        this.total -= amount;
      }
      this.editTo = this.total;
      this.updateAmount();
      this.modal = false;
    },
    confirm() {
      console.log({ confirm: this.code });
      if (this.code == "") {
        this.total = this.total;
      } else {
        const promo = this.promotions.filter(
          (res) => res.promotion_code == this.code
        );
        const amount = (this.total * promo[0].promotion_rate) / 100;

        this.total -= amount;
      }
      this.updateAmount();
    },
    check() {
      this.ok = true;
    },
    updateAmount() {
      this.ok = false;
      const id = this.users[0].id;
      const Data = {
        id: id,
        amount: this.total,
        code: this.code,
      };
      axios
        .put("http://localhost:3000/update/amount", Data)
        .then((res) => {
          console.log(res);
        })
        .catch((e) => console.log(e));
    },
    imagePath(file_path) {
      if (file_path) {
        return "http://localhost:3000/" + file_path;
      } else {
        return "https://bulma.io/images/placeholders/128x128.png";
      }
    },
    cancelst1(del) {
      const result = confirm(
        `Are you sure you want to delete Stadium1 Day \'${del.day}\' and Time \'${del.time}\'`
      );
      const data = { id: del.id };
      if (result) {
        axios
          .post(`http://localhost:3000/delete/bookingst1`, data)
          .then(() => {
            this.stadium1 = this.stadium1.filter((p) => p.id == del.id);
            console.log("Success");
          })
          .catch((error) => {
            alert(error.response.data.message);
          });
      }
    },
    cancelst2(del) {
      const result = confirm(
        `Are you sure you want to delete Stadium2 Day \'${del.day}\' and Time \'${del.time}\'`
      );
      const data = { id: del.id };
      if (result) {
        axios
          .post(`http://localhost:3000/delete/bookingst2`, data)
          .then(() => {
            this.stadium2 = this.stadium2.filter((p) => p.id == del.id);
            console.log("Success");
          })
          .catch((error) => {
            alert(error.response.data.message);
          });
      }
    },
  },
};
</script>